decode_msg_zipkin_endpoint(Bin, TrUserData) ->
    dfp_read_field_def_zipkin_endpoint(Bin, 0, 0,
				       id(<<>>, TrUserData),
				       id(<<>>, TrUserData),
				       id(<<>>, TrUserData), id(0, TrUserData),
				       TrUserData).

