    TrUserData = proplists:get_value(user_data, Opts),
    case MsgName of
      zipkin_span ->
	  v_msg_zipkin_span(Msg, [MsgName], TrUserData);
      zipkin_endpoint ->
	  v_msg_zipkin_endpoint(Msg, [MsgName], TrUserData);
      zipkin_annotation ->
	  v_msg_zipkin_annotation(Msg, [MsgName], TrUserData);
      zipkin_list_of_spans ->
	  v_msg_zipkin_list_of_spans(Msg, [MsgName], TrUserData);
      zipkin_report_response ->
	  v_msg_zipkin_report_response(Msg, [MsgName],
				       TrUserData);
      _ -> mk_type_error(not_a_known_message, Msg, [])
    end.


