	   v_type_fixed64(F6, [timestamp | Path], TrUserData)
    end,
    if F7 == undefined -> ok;
       true -> v_type_uint64(F7, [duration | Path], TrUserData)
    end,
    if F8 == undefined -> ok;
       true ->
